import React, { Component } from 'react';
import { render } from 'react-dom';
import {RadialGauge} from 'react-canvas-gauges';
import './style.css';

class App extends Component {
  constructor() {
    super();
    this.state = {
      name: 'React'
    };
  }

  componentDidMount() {
    const newLimit = () => {
      return Math.random() * 50
    }
    let limit = newLimit();
    let currentTemp = 0, directionUp = true;

    this.interval = setInterval(()=> {
        //currentTemp = directionUp ? (currentTemp + 1): (currentTemp - 1);
        fetch("http://localhost:8000/muestra/sensor/Humedad")
        .then((response) => response.json())

        if (((currentTemp >= limit) && directionUp) || 
        ((currentTemp <= limit) && !directionUp)) {
          let nextLimit = newLimit();
          if (nextLimit < currentTemp) {
            directionUp = false;
          }
          else {
            directionUp = true;
          }
        }

        this.setState({
          temperature: currentTemp
        })
        console.log("json: " )
        console.log(currentTemp)
        console.log("value: " )
        console.log(currentTemp['value'])

    }, 200);
  }

  componentWillUnmount() {
    if (this.interval) {
      clearInterval(this.interval)
    }
  }

  render() {
    return (
      <div>
        <RadialGauge
   units='Â°C'
   title='Temperature'
   value={this.state.temperature}
   minValue={0}
   maxValue={50}
   majorTicks={['0', '5', '15', '20', '25', '30', '35', '40', '45', '50']}
   minorTicks={2}
></RadialGauge>
      </div>
    );
  }
}

render(<App />, document.getElementById('root'));
